components/products/availability:
{{#if product}}
    {{product.availability}}
    {{#if product.availability '===' ''}}
        {{#if product.stock_level '===' 0}}
            <span class="outstock">Out of Stock</span>
        {{else}}
            {{#if product.can_purchase}}
                <span class="instock">In Stock</span>
            {{else}}
                <span class="outstock">Out of Stock</span>
            {{/if}}
        {{/if}}
    {{/if}}
{{else}}
    {{#if pre_order}}
        <span class="card-text--preOrder">{{lang 'products.pre_order'}}</span>
    {{else if stock_level '>' 0}}
        <span class="instock">In Stock</span>
    {{else if stock_level '===' 0}}
        <span class="outstock">Out of Stock</span>
    {{else}}
        {{#if (occurrences availability 'In Stock') '>' 0}}<span class="instock">In Stock</span>{{/if}}
        {{#if (occurrences availability 'Not in Stock') '>' 0}}<span class="outstock">Out of Stock'</span>{{/if}}
    {{/if}}
{{/if}}

components/common/share:
{{#if settings.add_this.buttons}}
    <div class="addthis_toolbox addthis_32x32_style" addthis:url="{{url}}"
        addthis:title="{{title}}">
        <div class="socialLinks-heading">Share This Product</div>
        <ul class="socialLinks">
            {{#each settings.add_this.buttons}}
                <li class="socialLinks-item socialLinks-item--{{service}}">
                    <a class="addthis_button_{{service}} icon icon--{{service}}" {{{annotation}}}>
                        {{#if service '===' 'google_plusone'}}
                            <!-- No custom icons allowed with g+ -->
                        {{/if}}

                        {{#if service '===' 'facebook'}}
                            <svg>
                                <use xlink:href="#icon-facebook"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'email'}}
                            <svg>
                                <use xlink:href="#icon-envelope"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'print'}}
                            <svg>
                                <use xlink:href="#icon-print"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'twitter'}}
                            <svg>
                                <use xlink:href="#icon-twitter"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'linkedin'}}
                            <svg>
                                <use xlink:href="#icon-linkedin"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'google'}}
                            <svg>
                                <use xlink:href="#icon-google"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'pinterest'}}
                            <svg>
                                <use xlink:href="#icon-pinterest"/>
                            </svg>
                        {{/if}}

                        {{#if service '===' 'facebook_like'}}
                            <svg>
                                <use xlink:href="#icon-facebook_like"/>
                            </svg>
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
        <script type="text/javascript"
                src="//s7.addthis.com/js/300/addthis_widget.js#pubid=ra-4e94ed470ee51e32"></script>
        <script>
            if (typeof(addthis) === "object") {
                addthis.toolbox('.addthis_toolbox');
            }
        </script>
    </div>
{{/if}}

components/products/product-view-suggest:
{{!
    Params:
    - @index
    - name: custom field name
    - value: custom field value
}}

<div class="productView-suggests">
    <div class="productView-section-heading">Suggested Accessories</div>
    <div class="productCarousel" data-slick='{
        "dots": false,
        "infinite": false,
        "mobileFirst": true,
        "slidesToShow": 2,
        "slidesToScroll": 2,
        "responsive": [
            {
                "breakpoint": 1420,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 5
                }
            },
            {
                "breakpoint": 1260,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 5
                }
            },
            {
                "breakpoint": 800,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 4
                }
            },
            {
                "breakpoint": 550,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 2
                }
            }
        ]
    }'>
        {{#each (split value ',')}}
            <div class="productCarousel-slide"><article class="card" data-remote-product data-product-id="{{this}}"></article></div>
        {{/each}}
    </div>
</div>
