components/emthemes-modez/banners/lapari:
{{#if image}}{{#if image '!=' ' '}}
<div class="emthemesModez-banner emthemesModez-banner--laparis5 {{#if style}}emthemesModez-banner--laparis5-{{style}}{{/if}}">
	<figure class="emthemesModez-banner-figure">
		<a href="{{url}}"><img class="emthemesModez-banner-image" src="{{image}}" alt="{{title}}" title="{{title}}" /></a>
	</figure>
	<figcaption class="emthemesModez-banner-figcaption">
		<div class="emthemesModez-banner-figcaption-body">

			{{#if title '!=' ''}}{{#if title '!=' ' '}}			
			<h4 class="emthemesModez-banner-figcaption-title"><div class="emthemesModez-banner-textBg">{{{title}}}</div></h4>
			{{/if}}{{/if}}

			{{#if text '!=' ''}}{{#if text '!=' ' '}}			
			<div class="emthemesModez-banner-figcaption-text"><div class="emthemesModez-banner-textBg">{{{text}}}</div></div>
			{{/if}}{{/if}}

			{{#if button '!=' ''}}{{#if button '!=' ' '}}
			<a href="{{url}}" class="button button--primary emthemesModez-banner-figcaption-button">{{{button}}}</a>
			{{/if}}{{/if}}
		</div>
	</figcaption>
</div>
{{/if}}{{/if}}
