components/products/top:
{{#unless hide_heading}}
<h2 class="page-heading">{{lang 'products.top' }}</h2>
{{/unless}}


{{#if display_type '==' 'carousel'}}
    {{> components/products/carousel products=products}}
{{else if display_type '==' 'grid'}}
    <ul class="productGrid productGrid--maxCol{{ columns }} {{#if style}}productGrid--{{style}}{{/if}}" data-product-type="top_sellers">
        {{#each products}}
            <li class="product">
                {{>components/products/card theme_settings=../theme_settings customer=../customer show_rating=../settings.show_product_rating show_compare=../show_compare show_wishlist=../show_wishlist}}
            </li>
        {{/each}}
    </ul>
{{else}}
    <ul class="productList productList--maxCol{{ columns }} productList--block {{#if style}}productGrid--{{style}}{{/if}}" data-product-type="top_sellers">
        {{#each products}}
            <li class="product">
                {{>components/products/list-item 
                	show_rating=false
                	summary=false
                    show_compare=../show_compare
                    show_wishlist=../show_wishlist
                	show_rating=../settings.show_product_rating 
                	theme_settings=../theme_settings customer=../customer}}
            </li>
        {{/each}}
    </ul>	
{{/if}}

components/products/carousel:
{{#if style '==' 'laparis4'}}
<section class="productCarousel"
    data-slick='{
        "dots": false,
        "infinite": false,
        "mobileFirst": true,
        "slidesToShow": 2,
        "slidesToScroll": 2,
        "responsive": [
            {
                "breakpoint": 1420,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": {{ columns }}
                }
            },
            {
                "breakpoint": 1260,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": {{#if columns ">" 6}}6{{else}}{{columns}}{{/if}}
                }
            },
            {
                "breakpoint": 800,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 4
                }
            },
            {
                "breakpoint": 550,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 2
                }
            }
        ]
    }'
>
    {{#each products}}
    <div class="productCarousel-slide">
        {{> components/products/card theme_settings=../theme_settings customer=../customer show_rating=../settings.show_product_rating show_compare=../show_compare show_wishlist=../show_wishlist style=../style}}
    </div>
    {{/each}}
</section>
{{else}}
<section class="productCarousel"
    data-slick='{
        "dots": false,
        "infinite": false,
        "mobileFirst": true,
        "slidesToShow": 2,
        "slidesToScroll": 2,
        "responsive": [
            {
                "breakpoint": 1260,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": {{ columns }}
                }
            },
            {
                "breakpoint": 800,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 5
                }
            },
            {
                "breakpoint": 550,
                "settings": {
                    "slidesToScroll": 3,
                    "slidesToShow": 3
                }
            }
        ]
    }'
>
    {{#each products}}
    <div class="productCarousel-slide">
        {{> components/products/card theme_settings=../theme_settings customer=../customer show_rating=../settings.show_product_rating show_compare=../show_compare show_wishlist=../show_wishlist style=../style}}
    </div>
    {{/each}}
</section>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull"><i class="fa fa-star"></i></span>
    {{else}}
        <span class="icon icon--ratingEmpty"><i class="fa fa-star-o"></i></span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/products/price:
{{#or has_options product.options.length}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.with_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <link itemprop="availability" href="{{#if product.out_of_stock}}http://schema.org/OutOfStock{{else}}http://schema.org/InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <link itemprop="availability" href="{{#if product.out_of_stock}}http://schema.org/OutOfStock{{else}}http://schema.org/InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
        <!--
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        -->
        {{/if}}
    {{/if}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.with_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <link itemprop="availability" href="{{#if product.out_of_stock}}http://schema.org/OutOfStock{{else}}http://schema.org/InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <link itemprop="availability" href="{{#if product.out_of_stock}}http://schema.org/OutOfStock{{else}}http://schema.org/InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
        <!--
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        -->
        {{/if}}
    {{/if}}
{{/or}}

components/products/card-sale-badge:
{{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}}
    {{#if theme_settings.product_sale_badges '===' 'sash'}}
        <div class="sale-flag-sash">
            <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
        </div>
    {{else if theme_settings.product_sale_badges '===' 'topleft'}}
        <div class="sale-flag-side">
            <span class="sale-text">{{lang 'emthemesmodez.on_sale'}}</span>
        </div>
    {{else if theme_settings.product_sale_badges '===' 'burst'}}
        <div class="starwrap">
            <div class="sale-text-burst">{{lang 'emthemesmodez.on_sale'}}</div>
            <div class="sale-flag-star"></div>
        </div>
    {{/if}}
{{/or}}

components/products/availability:
{{#if product}}
    {{product.availability}}
    {{#if product.availability '===' ''}}
        {{#if product.stock_level '===' 0}}
            <span class="outstock">Out of Stock</span>
        {{else}}
            {{#if product.can_purchase}}
                <span class="instock">In Stock</span>
            {{else}}
                <span class="outstock">Out of Stock</span>
            {{/if}}
        {{/if}}
    {{/if}}
{{else}}
    {{#if pre_order}}
        <span class="card-text--preOrder">{{lang 'products.pre_order'}}</span>
    {{else if stock_level '>' 0}}
        <span class="instock">In Stock</span>
    {{else if stock_level '===' 0}}
        <span class="outstock">Out of Stock</span>
    {{else}}
        {{#if (occurrences availability 'In Stock') '>' 0}}<span class="instock">In Stock</span>{{/if}}
        {{#if (occurrences availability 'Not in Stock') '>' 0}}<span class="outstock">Out of Stock'</span>{{/if}}
    {{/if}}
{{/if}}
