components/common/header-laparis2:
{{#if banners.top}}
<div class="banners" data-banner-location="top">
    <div class="banner">
        {{{limit banners.top 1}}}
    </div>
</div>
{{/if}}
<header class="header header--{{theme_settings.header-style}} header--laparis2 {{#if carousel}}header--laparis2-carousel{{/if}}" role="banner">
    <a href="#" class="mobileMenu-toggle" data-mobile-menu-toggle="menu">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>

    {{#if theme_settings.show_top_header}}
    <div class="emthemesModez-header-topSection">
        <div class="container">
            <ul>
                <li>{{{lang 'emthemesmodez.header.top.text1'}}}</li>
                <li>{{{lang 'emthemesmodez.header.top.text2'}}}</li>
                <li>{{{lang 'emthemesmodez.header.top.text3'}}}</li>
            </ul>
        </div>
    </div>
    {{/if}}

    <div class="emthemesModez-header-userSection emthemesModez-header-userSection--logo-{{theme_settings.logo-position}}">
        {{#if theme_settings.logo-position '==' 'center'}}
            {{> components/common/navigation-left}}
            {{> components/common/navigation-right}}
        {{else}}
            {{> components/common/navigation}}
        {{/if}}

        {{#if template_file '===' 'pages/home'}}
            <h1 class="header-logo header-logo--{{theme_settings.logo-position}}">
                {{> components/common/store-logo}}
            </h1>
        {{else}}
            <div class="header-logo header-logo--{{theme_settings.logo-position}}">
                {{> components/common/store-logo}}
            </div>
        {{/if}}

        <div class="navPages-container" id="menu" data-menu>
            {{> components/common/navigation-menu}}
        </div>
    </div>



</header>

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/navigation-mega:
{{#if mega_style '!==' ' '}}

    <a class="navPages-action has-subMenu" href="{{url}}" 
        data-collapsible="navPages-{{#if type '===' 'category'}}{{id}}{{else}}page-{{@index}}{{/if}}"
        data-collapsible-disabled-breakpoint="large"
        data-collapsible-disabled-state="open"
        data-collapsible-enabled-state="closed"
        data-mega="{{type}}-{{mega_style}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
        {{> components/emthemes-modez/common/navigation-label}}
    </a>    

    {{#if mega_style '===' '1'}}
        {{> components/common/navigation-list-mega1}}
    {{else if mega_style '===' '2'}}
        {{> components/common/navigation-list-mega2}}
    {{else if mega_style '===' '3'}}
        {{> components/common/navigation-list-mega3}}
    {{else if mega_style '===' '4'}}
        {{> components/common/navigation-list-mega4}}
    {{else if mega_style '===' '5'}}
        {{> components/common/navigation-list-mega5}}
    {{else if mega_style '===' '6'}}
        {{> components/common/navigation-list-mega6}}
    {{else if mega_style '===' '7'}}
        {{> components/common/navigation-list-mega7}}
    {{else if mega_style '===' '8'}}
        {{> components/common/navigation-list-mega8}}
    {{else if mega_style '===' '9'}}
        {{> components/common/navigation-list-mega9}}
    {{else if mega_style '===' '10'}}
        {{> components/common/navigation-list-mega10}}
    {{/if}}

{{else}}

    {{#if type '===' 'category'}}
        {{> components/common/navigation-list}}
    {{else}}
        {{> components/common/navigation-page-list}}
    {{/if}}

{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu" href="{{url}}" 
    data-collapsible="navPages-{{id}}"
    data-collapsible-disabled-breakpoint="large"
    data-collapsible-disabled-state="open"
    data-collapsible-enabled-state="closed">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    {{> components/emthemes-modez/common/navigation-label}}
</a>
<div class="navPage-subMenu navPage-subMenu--default" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#if (lang (concat 'emthemesmodez.megamenu.style_' id)) '==' 'banner-half-left'}}
            <li class="navPage-subMenu-item navPage-subMenu-item--banner-half-left">
                <a class="navPage-subMenu-action navPages-action" href="{{url}}"><img src="{{lang (concat 'emthemesmodez.megamenu.cat_image_' id)}}" alt="{{name}}"></a>
            </li>
        {{else if (lang (concat 'emthemesmodez.megamenu.style_' id)) '==' 'banner-half-right'}}
            <li class="navPage-subMenu-item navPage-subMenu-item--banner-half-right">
                <a class="navPage-subMenu-action navPages-action" href="{{url}}"><img src="{{lang (concat 'emthemesmodez.megamenu.cat_image_' id)}}" alt="{{name}}"></a>
            </li>
        {{else}}
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}} {{> components/emthemes-modez/common/navigation-label}}</a>
            {{#if (lang (concat 'emthemesmodez.megamenu.style_' id)) '==' 'banner-quarter'}}
            <div class="navPage-childList navPage-childList--text is-open">
                <a href="{{url}}"><img src="{{lang (concat 'emthemesmodez.megamenu.cat_image_' id)}}" alt="{{name}}"></a>
            </div>
            {{/if}}
        </li>
        {{/if}}
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        data-collapsible="navPages-{{id}}"
                        data-collapsible-disabled-breakpoint="medium"
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                        {{> components/emthemes-modez/common/navigation-label}}
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}} {{> components/emthemes-modez/common/navigation-label}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}} {{> components/emthemes-modez/common/navigation-label}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}} {{> components/emthemes-modez/common/navigation-label}}</a>
{{/if}}
